`timescale 1ns / 1ps

module testbench;
    reg d;
    reg ena;
    wire q;
    reg error_flag; // Line 7 had initialization, which is moved to initial begin block

    // Instantiate the DUT (Device Under Test)
    top_module DUT (
        .d(d),
        .ena(ena),
        .q(q)
    );

    // Test stimulus
    initial begin
        error_flag = 0; // Initialize here as per correct Verilog practices
        // Test case 1 - Check latch holds value when ena is low
        d = 1; ena = 0;
        #10; // Wait for stable state
        if (q !== 1) begin
            $display("Test Case 1 Failed: q did not hold when ena = 0");
            error_flag = 1;
        end

        // Test case 2 - Check latch updates value when ena is high
        d = 0; ena = 1;
        #10; // Wait for stable state
        if (q !== 0) begin
            $display("Test Case 2 Failed: q did not update to 0 when ena = 1");
            error_flag = 1;
        end

        ena = 0;
        #10; // Wait for stable state
        if (q !== 0) begin
            $display("Test Case 3 Failed: q did not hold at 0 when ena was toggled");
            error_flag = 1;
        end

        // Test case 4 - Check if latch can update to 1
        d = 1; ena = 1;
        #10; // Allow time for the change
        if (q !== 1) begin
            $display("Test Case 4 Failed: q did not update to 1 when ena = 1 and d = 1");
            error_flag = 1;
        end

        // Test case 5 - Check hold condition again
        ena = 0;
        #10; // Wait for stable state
        if (q !== 1) begin
            $display("Test Case 5 Failed: q did not hold at 1 when ena = 0 again");
            error_flag = 1;
        end

        // Check if any test cases failed
        if (error_flag == 0) begin
            $display("all test cases passed");
        end

        $finish; // End simulation
    end
endmodule

`timescale 1ns / 1ps

module testbench;
    reg in1, in2, in3;
    wire out;
    integer i;

    // Instantiate the DUT (Device Under Test)
    top_module DUT (
        .in1(in1),
        .in2(in2),
        .in3(in3),
        .out(out)
    );

    initial begin
        // Initialize inputs
        in1 = 0; in2 = 0; in3 = 0;

        // Test cases
        for (i = 0; i < 8; i = i + 1) begin
            {in1, in2, in3} = i;  // Assign different combinations of inputs
            #10; // Wait for some time for output to stabilize
        end

        // Check outputs for correctness
        if (out == ^{in1 ~^ in2, in3}) begin
            $display("all test cases passed");
        end
    end
endmodule

`timescale 1ns / 1ps

module testbench;
    // Inputs
    reg [3:0] in;

    // Outputs
    wire [2:0] out_both;
    wire [3:1] out_any;
    wire [3:0] out_different;

    // Instantiate the DUT (Device Under Test)
    top_module dut (
        .in(in),
        .out_both(out_both),
        .out_any(out_any),
        .out_different(out_different)
    );

    // Variable to track testing status
    reg all_passed;

    initial begin
        // Initialize all inputs
        in = 4'b0000;
        all_passed = 1'b1;

        // Stimulus and checking
        #10;
        in = 4'b1111; // Test all 1s
        #10;
        if ((out_both !== 3'b111) || (out_any !== 3'b111) || (out_different !== 4'b0000)) begin
            all_passed = 1'b0;
        end

        in = 4'b1010; // Test alternating 1s and 0s
        #10;
        if ((out_both !== 3'b000) || (out_any !== 3'b111) || (out_different !== 4'b1111)) begin
            all_passed = 1'b0;
        end

        in = 4'b0101; // Test alternating 0s and 1s
        #10;
        if ((out_both !== 3'b000) || (out_any !== 3'b111) || (out_different !== 4'b1111)) begin
            all_passed = 1'b0;
        end

        in = 4'b1000; // Test single 1 at highest bit
        #10;
        if ((out_both !== 3'b000) || (out_any !== 3'b100) || (out_different !== 4'b0010)) begin
            all_passed = 1'b0;
        end

        in = 4'b0001; // Test single 1 at lowest bit
        #10;
        if ((out_both !== 3'b000) || (out_any !== 3'b011) || (out_different !== 4'b0100)) begin
            all_passed = 1'b0;
        end

        // Check if all test cases passed
        if (all_passed) begin
            $display("all test cases passed");
        end

        // Finish testing
        $finish;
    end

endmodule

