module testbench;

    reg in;
    reg [3:0] state;
    wire [3:0] next_state;
    wire out;
    integer errors;

    // Instantiate the DUT (Device Under Test)
    top_module dut (
        .in(in),
        .state(state),
        .next_state(next_state),
        .out(out)
    );

    initial begin
        errors = 0;
        // Test case 1: State A with input 0
        state = 4'b0001; in = 0;
        #10;
        if (next_state !== 4'b0001 || out !== 0) begin
            $display("Test case 1 failed");
            errors = errors + 1;
        end

        // Test case 2: State A with input 1
        state = 4'b0001; in = 1;
        #10;
        if (next_state !== 4'b0010 || out !== 0) begin
            $display("Test case 2 failed");
            errors = errors + 1;
        end

        // Test case 3: State B with input 0
        state = 4'b0010; in = 0;
        #10;
        if (next_state !== 4'b0100 || out !== 0) begin
            $display("Test case 3 failed");
            errors = errors + 1;
        end

        // Test case 4: State B with input 1
        state = 4'b0010; in = 1;
        #10;
        if (next_state !== 4'b0010 || out !== 0) begin
            $display("Test case 4 failed");
            errors = errors + 1;
        end

        // Test case 5: State C with input 0
        state = 4'b0100; in = 0;
        #10;
        if (next_state !== 4'b0001 || out !== 0) begin
            $display("Test case 5 failed");
            errors = errors + 1;
        end

        // Test case 6: State C with input 1
        state = 4'b0100; in = 1;
        #10;
        if (next_state !== 4'b1000 || out !== 0) begin
            $display("Test case 6 failed");
            errors = errors + 1;
        end

        // Test case 7: State D with input 0
        state = 4'b1000; in = 0;
        #10;
        if (next_state !== 4'b0100 || out !== 1) begin
            $display("Test case 7 failed");
            errors = errors + 1;
        end

        // Test case 8: State D with input 1
        state = 4'b1000; in = 1;
        #10;
        if (next_state !== 4'b0010 || out !== 1) begin
            $display("Test case 8 failed");
            errors = errors + 1;
        end

        if (errors === 0) begin
            $display("all test cases passed");
        end
        $finish;
    end

endmodule

