        .in(in),
        .out(out)
    );

    // Test variables
    integer i;
    reg [31:0] expected_output;
    reg error_detected;

    initial begin
        // Initialize Inputs
        in = 0;
        error_detected = 0;

        // Add test cases here
        // Test case 1
        in = 32'h12345678; // input value
        expected_output = 32'h78563412; // expected reversed output
        #10; // wait for the output to be processed
        if (out !== expected_output) begin
            error_detected = 1; 
            $display("Test case 1 failed: input = %h, expected output = %h, got = %h", in, expected_output, out);
        end

        // Test case 2
