        #10;
        if (out !== expected_output) begin
            error_detected = 1; 
            $display("Test case 2 failed: input = %h, expected output = %h, got = %h", in, expected_output, out);
        end

        // Test case 3
        in = 32'hFFFFFFFF;
        expected_output = 32'hFFFFFFFF;
        #10;
        if (out !== expected_output) begin
            error_detected = 1; 
            $display("Test case 3 failed: input = %h, expected output = %h, got = %h", in, expected_output, out);
        end

        // Test case 4
        in = 32'h00000000;
        expected_output = 32'h00000000;
        #10;
        if (out !== expected_output) begin
            error_detected = 1; 
            $display("Test case 4 failed: input = %h, expected output = %h, got = %h", in, expected_output, out);
        end

        // Check if all tests passed
        if (error_detected == 0) begin
            $display("all test cases passed");
        end

        // Finish the simulation
