module testbench;

    // Test Inputs
    reg [254:0] in;

    // Outputs
    wire [7:0] out;

    // Instantiate the Unit Under Test (DUT)
    top_module dut(
        .in(in),
        .out(out)
    );

    // Function to count the number of 1s in binary representation
    function integer count_ones;
        input [254:0] data;
        integer count;
        integer i;
        begin
            count = 0;
            for (i = 0; i < 255; i = i + 1) begin
                count = count + data[i];
            end
            count_ones = count;
        end
    endfunction

    // Test Cases
    initial begin
        integer i;
        integer pass_counter;
        reg [7:0] expected_out;

        pass_counter = 0;

        // Random test cases
        for (i = 0; i < 100; i = i + 1) begin
            // Generate random input
            in = $random;

            // Calculate expected output using the function
            expected_out = count_ones(in);

            // Check the module output against expected output
            #10;    // Wait for output to stabilize
            if (out !== expected_out) begin
                $display("Test case Failed: Input = %b, Expected output = %d, Actual Output = %d",
                         in, expected_out, out);
            end else begin
                pass_counter = pass_counter + 1;
            end
        end

        // Display message if all test cases passed
        if (pass_counter == 100) begin
            $display("all test cases passed");
        end
        $finish;
    end

endmodule

