module testbench;
    reg mode, too_cold, too_hot, fan_on;
    wire heater, aircon, fan;

    // Instantiate the Device Under Test (DUT)
    top_module DUT (
        .mode(mode),
        .too_cold(too_cold),
        .too_hot(too_hot),
        .fan_on(fan_on),
        .heater(heater),
        .aircon(aircon),
        .fan(fan)
    );

    initial begin
        // Initialize inputs
        mode = 0; too_cold = 0; too_hot = 0; fan_on = 0;

        // Test case 1: Cooling mode, too hot, fan and air conditioner should turn on
        #10 mode = 0; too_hot = 1;
        #10 if (!(aircon == 1 && fan == 1 && heater == 0)) $display("Test case 1 failed");

        // Test case 2: Cooling mode, not too hot, all should be off
        #10 too_hot = 0;
        #10 if (!(aircon == 0 && fan == 0 && heater == 0)) $display("Test case 2 failed");

        // Test case 3: Heating mode, too cold, fan and heater should turn on
        #10 mode = 1; too_cold = 1;
        #10 if (!(heater == 1 && fan == 1 && aircon == 0)) $display("Test case 3 failed");

        // Test case 4: Heating mode, not too cold, all should be off
        #10 too_cold = 0;
        #10 if (!(heater == 0 && fan == 0 && aircon == 0)) $display("Test case 4 failed");

        // Test case 5: Fan on request, others conditions normal
        #10 fan_on = 1;
        #10 if (!(fan == 1)) $display("Test case 5 failed");

        // Test case 6: Fan on with cooling active
        #10 mode = 0; too_hot = 1;
        #10 if (!(aircon == 1 && fan == 1)) $display("Test case 6 failed");

        // Test case 7: Fan on with heating active
        #10 mode = 1; too_cold = 1;
        #10 if (!(heater == 1 && fan == 1)) $display("Test case 7 failed");

        // Check if all test cases passed
        #10 if (!$test$plusargs("Test case 1 failed") && !$test$plusargs("Test case 2 failed") &&
                !$test$plusargs("Test case 3 failed") && !$test$plusargs("Test case 4 failed") &&
                !$test$plusargs("Test case 5 failed") && !$test$plusargs("Test case 6 failed") &&
                !$test$plusargs("Test case 7 failed"))
            $display("all test cases passed");

        // Finish the simulation
        #10 $finish;
    end
endmodule

