module testbench;
    reg clk, reset;
    wire [3:1] ena;
    wire [15:0] q;

    top_module dut(
        .clk(clk),
        .reset(reset),
        .ena(ena),
        .q(q)
    );

    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // Clock with 10ns period
    end

    initial begin
        reset = 1; #10;
        reset = 0; #100;

        // Checking reset functionality
        if (q !== 16'b0) begin
            $display("Reset failed");
            $finish;
        end

        reset = 1; #10;
        reset = 0;

        // Simulation for several cycles to test counting and enable logic
        repeat (1000) @(posedge clk);

        if (q[3:0] == 9 && q[7:4] == 9 && q[11:8] == 9 && q[15:12] == 9) begin
            if (q != 16'b0) begin
                $display("Counting beyond 9999");
                $finish;
            end
        end

        // Testing rolling over and enabling signals
        reset = 1; #10;
        reset = 0;

        wait (q[3:0] == 9); #10;
        if (ena[1] !== 1) begin
            $display("Enable signal for tens place failed when ones place is 9.");
            $finish;
        end

        wait (q[7:4] == 9); #10;
        if (ena[2] !== 1) begin
            $display("Enable signal for hundreds place failed when tens place is 9.");
            $finish;
        end

        wait (q[11:8] == 9); #10;
        if (ena[3] !== 1) begin
            $display("Enable signal for thousands place failed when hundreds place is 9.");
            $finish;
        end

        wait (q == 16'd9999);
        @(posedge clk);
        if (q !== 16'b0) begin
            $display("Counter reset after reaching 9999 failed.");
            $finish;
        end

        $display("all test cases passed");
        $finish;
    end
endmodule

