module testbench;

    reg [2:0] in;
    wire [1:0] out;
    integer i;
    reg [1:0] expected_result;

    // Instantiate the Device Under Test (DUT)
    top_module dut(
        .in(in),
        .out(out)
    );

    initial begin
        // Test vectors
        for (i = 0; i < 8; i = i + 1) begin
            in = i;
            // Calculating expected result
            expected_result = (in[0] + in[1] + in[2]);

            #10; // Wait for output to stabilize

            if (out !== expected_result) begin
                $display("Test failed for input %b: Expected %b, got %b", in, expected_result, out);
                $finish; // Stop simulation on first failed test case
            end
        end
        $display("all test cases passed");
    end

endmodule

