module testbench;
    reg clk;
    reg reset;
    wire [3:0] q;

    // Instance of the device under test
    top_module dut (
        .clk(clk),
        .reset(reset),
        .q(q)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // 100MHz Clock
    end

    // Test cases
    initial begin
        // Initialize inputs
        reset = 0;

        // Test Case 1: Check reset functionality
        reset = 1; #10;
        reset = 0; #10;
        if (q !== 4'b0000) begin
            $display("Reset test failed at %t", $time);
            $finish;
        end

        // Test Case 2: Counting from 0 to 15
        repeat (16) begin
            @(posedge clk);
        end
        if (q !== 4'b1111) begin
            $display("Counting error at %t", $time);
            $finish;
        end

        // Reset and check again
        reset = 1; #10;
        reset = 0; #10;
        if (q !== 4'b0000) begin
            $display("Second reset test failed at %t", $time);
            $finish;
        end

        // If no failures, display success message
        $display("All test cases passed");
    end

endmodule

