module testbench;
    // Inputs to DUT
    reg clk;
    reg in;
    reg reset;

    // Output from DUT
    wire out;

    // Instantiate the Device Under Test (DUT)
    top_module DUT (
        .clk(clk),
        .in(in),
        .reset(reset),
        .out(out)
    );

    // Clock generator
    initial begin
        clk = 0;
        forever #5 clk = ~clk;
    end

    // Test cases
    initial begin
        // Apply reset
        reset = 1; #10;
        reset = 0;

        // Test case 1: State A -> A (in=0)
        in = 0;
        #10;
        if(out !== 0) begin
            $display("Test case failed: State A with input 0");
            $finish;
        end

        // Test case 2: State A -> B -> C (in=0, 1, 0)
        in = 1;
        #10;
        in = 0;
        #10;
        if(out !== 0) begin
            $display("Test case failed: State C with input 0");
            $finish;
        end

        // Test case 3: State C -> A -> B (in=0)
        in = 0;
        #10;
        if(out !== 0) begin
            $display("Test case failed: State B with input 0");
            $finish;
        end

        // Test case 4: State B -> B (in=1)
        in = 1;
        #10;
        if(out !== 0) begin
            $display("Test case failed: State B with input 1");
            $finish;
        end

        // Test case 5: State D -> B -> B (in=1, 1)
        in = 0; // Go to State D from C
        #10;
        in = 1; // Stay in State D
        #10;
        in = 1;
        #10;
        if(out !== 1) begin
            $display("Test case failed: State D with input 1");
            $finish;
        end

        // Test case 6: State D -> C -> A (in=0, 0)
        in = 0;
        #10;
        in = 0;
        #10;
        if(out !== 0) begin
            $display("Test case failed: State A with input 0 after C");
            $finish;
        end

        // If no test case has failed
        $display("All test cases passed");
        $finish;
    end
endmodule

