module testbench;

    // Inputs
    reg [5:0] y;
    reg w;

    // Outputs
    wire Y1;
    wire Y3;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .y(y), 
        .w(w), 
        .Y1(Y1), 
        .Y3(Y3)
    );

    // Test variables 
    integer errors = 0;

    // Display function
    task display_results;
        if (errors == 0) begin
            $display("all test cases passed");
        end
    endtask

    // Testing logic
    initial begin
        // Initialize Inputs
        y = 6'b000000;
        w = 0;

        // Wait for global reset
        #100;

        // Test case 1: State A and input 0 -> stays in A
        y = 6'b000001;
        w = 0;
        #10;
        if (Y1 !== 1'b0 || Y3 !== 1'b0) begin
            errors = errors + 1;
        end

        // Test case 2: State A and input 1 -> goes to B
        y = 6'b000001;
        w = 1;
        #10;
        if (Y1 !== 1'b1 || Y3 !== 1'b0) begin
            errors = errors + 1;
        end

        // Test case 3: State B and input 0 -> goes to D
        y = 6'b000010;
        w = 0;
        #10;
        if (Y1 !== 1'b0 || Y3 !== 1'b1) begin
            errors = errors + 1;
        end

        // Test case 4: State B and input 1 -> goes to C
        y = 6'b000010;
