    // Inputs to the DUT
    reg clk;
    reg reset;
    reg [31:0] in;

    // Output from the DUT
    wire [31:0] out;

    // Instantiate the Device Under Test (DUT)
    top_module DUT (
        .clk(clk),
        .reset(reset),
        .in(in),
        .out(out)
    );

    // Generate clock signal (50 MHz)
    initial begin
        clk = 0;
        forever #10 clk = ~clk; // Toggle every 10ns
    end

    int errors; // Correction: Declaration moved to appropriate scope

    // Test Cases
    initial begin
        $monitor("Time=%t, clk=%b, reset=%b, in=%h, out=%h", $time, clk, reset, in, out);

        // Initialize Inputs
        in = 32'hFFFFFFFF; // start with all 1's
        reset = 1; // Activate reset
        #20; // Wait 20ns for reset to take effect
        reset = 0;

        // Test case 1: Signal transition from 1 to 0 for bit 5
        in[5] = 0; 
        #20; // Change after one clock cycle
        if (out[5] !== 1'b1) begin
            $display("Test case 1 failed");
        end

        // Test case 2: Other bits should not affect bit 5 output
        in = 32'hFFFF_DFFF; // Change bit 13 from 1 to 0, bit 5 stays 0
        #20;
        if ((out[5] !== 1'b1) || (out[13] !== 1'b0)) begin
            $display("Test case 2 failed");
        end

        // Test case 3: Checking reset functionality
        reset = 1;
        #20; // After reset, output should be cleared
        if (|out !== 32'h0) begin
            $display("Test case 3 failed");
        end

        // Continuous checks for all bits
        reset = 0;
        for (int i = 0; i < 32; i++) begin
            in = 32'hFFFFFFFF;
            #20;
            in[i] = 0;
            #20;
