module tb_top_module;

reg clk;
reg areset;
reg bump_left;
reg bump_right;
reg ground;
reg dig;
wire walk_left;
wire walk_right;
wire aaah;
wire digging;

top_module dut (
    .clk(clk),
    .areset(areset),
    .bump_left(bump_left),
    .bump_right(bump_right),
    .ground(ground),
    .dig(dig),
    .walk_left(walk_left),
    .walk_right(walk_right),
    .aaah(aaah),
    .digging(digging)
);

initial begin
    clk = 0;
    forever #5 clk = ~clk;
end

initial begin
    areset = 0;
    bump_left = 0;
    bump_right = 0;
    ground = 1;
    dig = 0;

    // Reset the device
    @(negedge clk) areset = 1;
    @(negedge clk) areset = 0;

    // Check initial state to walk left
    @(negedge clk);
    assert(walk_left === 1);

    // Lemming walks left and hits left bump
    @(negedge clk) bump_left = 1;
    @(negedge clk);
    assert(walk_right === 1 && aaah === 0);

    // Clear bumps and keep walking right
    @(negedge clk) bump_left = 0;
    @(negedge clk);
    assert(walk_right === 1);

    // Bump on the right, should walk left
    @(negedge clk) bump_right = 1;
    @(negedge clk);
    assert(walk_left === 1);

    // Falling simulation
    @(negedge clk) ground = 0;
    @(negedge clk);
    assert(aaah === 1 && walk_left === 1);

    // Hit ground again, continue walking left
    @(negedge clk) ground = 1;
    @(negedge clk);
    assert(aaah === 0 && walk_left === 1);

    // Start digging
    @(negedge clk) dig = 1;
    @(negedge clk);
    assert(digging === 1);

    // Ground disappears while digging
    @(negedge clk) ground = 0;
    @(negedge clk);
    assert(aaah === 1 && digging === 0);

    // End of test, check for all cases passed
    if (!$isunknown(walk_left) && !$isunknown(walk_right) && !$isunknown(aaah) && !$isunknown(digging)) begin
        $display("all test cases passed");
    end

    $finish;
end

endmodule

