module testbench;
    reg clk, areset, bump_left, bump_right, ground;
    wire walk_left, walk_right, aaah;

    top_module DUT(
        .clk(clk),
        .areset(areset),
        .bump_left(bump_left),
        .bump_right(bump_right),
        .ground(ground),
        .walk_left(walk_left),
        .walk_right(walk_right),
        .aaah(aaah)
    );

    initial begin
        clk = 0;
        forever #5 clk = ~clk;
    end

    initial begin
        // Initialize all signals
        areset = 0; bump_left = 0; bump_right = 0; ground = 1;

        // Assert asynchronous reset
        #10;
        areset = 1; #10; areset = 0;

        // Check initial state (Lemming should be walking left)
        if (walk_left !== 1 || walk_right !== 0) begin
            $display("Test case failed: Initial state error");
            $finish;
        end

        // Test bump_left
        bump_left = 1; #10; bump_left = 0;
        if (walk_left !== 0 || walk_right !== 1) begin
            $display("Test case failed: Error handling bump_left");
            $finish;
        end

        // Test bump_right while walking right
        bump_right = 1; #10; bump_right = 0;
        if (walk_left !== 1 || walk_right !== 0) begin
            $display("Test case failed: Error handling bump_right");
            $finish;
        end

        // Test fall and resume previous direction (walk_left)
        ground = 0; #10;
        if (aaah !== 1) begin
            $display("Test case failed: Lemming did not fall correctly");
            $finish;
        end
        ground = 1; #10;
        if (walk_left !== 1 || walk_right !== 0) begin
            $display("Test case failed: Error resuming walk_left after fall");
            $finish;
        end

        // Test being bumped while falling does not affect direction
        bump_left = 1; ground = 0; #10; bump_left = 0; ground = 1; #10;
        if (walk_left !== 1 || walk_right !== 0) begin
            $display("Test case failed: Bump while falling affected direction");
            $finish;
        end

        // Test simultaneous bumps
        bump_left = 1; bump_right = 1; ground = 1; #10; bump_left = 0; bump_right = 0; 
        if (walk_left !== 0 || walk_right !== 1) begin
            $display("Test case failed: Simultaneous bumps not handled correctly");
            $finish;
        end

        // Test state persistence over cycles without inputs
        #20;
        if (walk_left !== 0 || walk_right !== 1) begin
            $display("Test case failed: State not persistent without bumps");
            $finish;
        end

        $display("all test cases passed");
        $finish;
    end
endmodule

