module testbench();

    reg clk;
    reg [7:0] in;
    wire [7:0] anyedge;
    reg [7:0] expected_anyedge;
    reg [7:0] prev_in;
    integer i;
    integer error_count;

    // Instantiate the Device Under Test (DUT)
    top_module DUT (
        .clk(clk),
        .in(in),
        .anyedge(anyedge)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // 100MHz Clock
    end

    // Stimulus and checking
    initial begin
        // Initialize
        in = 8'h00;
        prev_in = 8'h00;
        error_count = 0;
        #10; // Wait for the initial state to settle

        // Test vectors
        // Each test case lasts 2 clock cycles
        for (i = 0; i < 256; i = i + 1) begin
            in = i[7:0]; // Set inputs
            @(posedge clk);  // Wait for the clock edge
            expected_anyedge = (in ^ prev_in) & in; // Calculate expected outputs
            @(posedge clk);  // Check the output after one more clock

            if (anyedge !== expected_anyedge) begin
                $display("Mismatch: input = %b, expected anyedge = %b, received anyedge = %b", in, expected_anyedge, anyedge);
                error_count = error_count + 1;
            end

            prev_in = in; // Update previous input
        end

        // Check for pass/fail status
        if (error_count == 0) begin
            $display("all test cases passed");
        end

        $finish; // End simulation
    end

endmodule

