    top_module DUT (
        .clk(clk),
        .areset(areset),
        .predict_valid(predict_valid),
        .predict_taken(predict_taken),
        .predict_history(predict_history),
        .train_mispredicted(train_mispredicted),
        .train_taken(train_taken),
        .train_history(train_history)
    );

    // Clock generator
    always #5 clk = ~clk;

    initial begin
        // Initialize all signals
        clk = 0;
        areset = 1;
        predict_valid = 0;
        predict_taken = 0;
        train_mispredicted = 0;
        train_taken = 0;
        train_history = 32'd0;

        // Initial reset
        #10;
        areset = 0;
        #10;
        areset = 1;
        #10;
        areset = 0;

        // Test scenarios
        // Case 1: No prediction or misprediction
        #10;
        check_history(32'd0);

        // Case 2: Simple prediction
        predict_taken = 1;
        predict_valid = 1; 
        #10;
        predict_valid = 0;
        check_history(32'h00000001);

        // Case 3: Misprediction after prediction
        predict_taken = 0;
        predict_valid = 1; 
        train_mispredicted = 1;
        train_taken = 1;
        train_history = 32'h00000001;
        #10;
        predict_valid = 0;
        train_mispredicted = 0;
        check_history(32'h00000003);

        // Case 4: Misprediction takes precedence over concurrent prediction
        predict_taken = 1;
        predict_valid = 1; 
        train_mispredicted = 1;
        train_taken = 0;
        train_history = 32'h7fffffff;
        #10;
        predict_valid = 0;
        train_mispredicted = 0;
        check_history(32'hffffffff);

        // If all the cases passed
        if (!$test$plusargs("error")) $display("all test cases passed");

        $finish;
    end

    task check_history;
        input [31:0] expected_history;
        begin
            if (predict_history !== expected_history) begin
                $display("Test failed: Expected %h, but got %h at time %t",
                         expected_history, predict_history, $time);
                $display("Error"); // Flag error, all test cases will not pass
            end
        end
    endtask
