    wire [6:0] expected_updated_history; // Simulation variable for expected update in history
    wire [6:0] train_hist_verify; // Variable to verify training history

    // Test sequence
    initial begin
        // Initialize inputs
        areset = 1;  // Assert reset
        predict_valid = 0;
        predict_pc = 0;
        train_valid = 0;
        train_taken = 0;
        train_mispredicted = 0;
        train_history = 0;
        train_pc = 0;

