        @ (negedge clk);
        predict_valid = 1;
        predict_pc = 7'd40;
        @ (negedge clk);
        train_valid = 1;
        train_taken = 1;
        train_mispredicted = 0;
        train_history = predict_history;
        train_pc = 7'd20;

        @ (negedge clk);
        predict_valid = 0;
        predict_pc = 0;
        train_valid = 0;
        train_taken = 0;
        train_mispredicted = 0;
