    wire out;
    integer i, error_count;

    // Instantiate the Device Under Test (DUT)
    top_module DUT (
        .a(a),
        .b(b),
        .c(c),
        .d(d),
        .out(out)
    );

    initial begin
        // Initialize all variables
        a = 0; b = 0; c = 0; d = 0;
        test_passed = 1;
        error_count = 0;

        // Test cases based on the Karnaugh map and epsilon behavior
        for (i = 0; i < 16; i = i + 1) begin
            {a, b, c, d} = i;  // Assign binary count to inputs
            #10;  // Wait for output to stabilize

            // Check if output matches expected values from the K-map, except don't-cares
            case ({a, b, c, d})
                4'b0000, 4'b0001: if (out !== 1) test_passed = 0;
                4'b0010, 4'b0011: if (out !== 0) test_passed = 0;
                4'b0100, 4'b0101: if (out !== 0) test_passed = 0;
                4'b0110, 4'b0111: if (out != 1'b0 && out != 1'b1) test_passed = 0;  // Don't-care
                4'b1000, 4'b1001: if (out !== 0) test_passed = 0;
                4'b1010, 4'b1011: if (out !== 1) test_passed = 0;
                4'b1100, 4'b1101: if (out !== 1) test_passed = 0;
                4'b1110, 4'b1111: if (out !== 1) test_passed = 0;
                default: test_passed = 0;
            endcase
        end;

        if (test_passed) begin
            $display("all test cases passed");
        end;

        // Finish the simulation
        $finish;
    end

endmodule

