module top_module_tb;

reg clk;
reg load;
reg [9:0] data;
wire tc;

top_module dut(
    .clk(clk),
    .load(load),
    .data(data),
    .tc(tc)
);

// Generate clock
always #5 clk = ~clk; // Clock period of 10ns

initial begin
    clk = 0;
    load = 0;
    data = 0;

    // Test 1: Load the counter and check if it counts down to zero and tc asserts
    @(posedge clk) load = 1; data = 10'd5;
    @(posedge clk) load = 0; data = 10'd0;
    repeat(5) @(posedge clk);
    if (tc !== 1'b1) $display("Test case 1 Failed: tc did not assert after loading and counting down.");

    // Reset for next test
    @(posedge clk) load = 1; data = 10'd0;
    @(posedge clk) load = 0;

    // Test 2: Reload counter while it's still counting
    @(posedge clk) load = 1; data = 10'd3;
    @(posedge clk) load = 0;
    @(posedge clk);
    @(posedge clk) load = 1; data = 10'd4; // Reload before it hits zero
    repeat(4) @(posedge clk);
    if (tc !== 1'b1) $display("Test case 2 Failed: tc did not assert after reload and countdown.");

    // Test 3: Verify that tc stays at 0 until counter is reloaded
    repeat(5) @(posedge clk); 
    if (tc === 1'b1) $display("Test case 3 Failed: tc did not remain low after countdown.");

    @(posedge clk) load = 0;
    // Pass Condition Check
    #10
    if (!$test$plusargs("Test case")) $display("all test cases passed");

    // Finish simulation
    $finish;
end

