module testbench;
    // Inputs to the DUT
    reg clk;
    reg reset;
    reg data;
    reg done_counting;
    reg ack;

    // Outputs from the DUT
    wire shift_ena;
    wire counting;
    wire done;

    // Internal signal to hold counting process
    reg counting_reg;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .clk(clk),
        .reset(reset),
        .data(data),
        .shift_ena(shift_ena),
        .counting(counting),
        .done_counting(done_counting),
        .done(done),
        .ack(ack)
    );

    // Clock generation
    always #5 clk = ~clk; // Clock with period 10ns

    // Test procedure
    initial begin
        // Initialize inputs
        clk = 0;
        reset = 1;
        data = 0;
        done_counting = 0;
        ack = 0;

        // Apply reset
        #10;
        reset = 0;

        // Start pattern detection test
        @(negedge clk);
        send_pattern(1'b1);
        send_pattern(1'b1);
        send_pattern(1'b0);
        send_pattern(1'b1); // 1101 is the triggering pattern

        // Check shift_ena is asserted for 4 cycles
        if(shift_ena !== 1) $display("Test Failed: `shift_ena` should be asserted");
        @(negedge clk); // 1st cycle
        if(shift_ena !== 1) $display("Test Failed: `shift_ena` should be asserted for 4 cycles");
        @(negedge clk); // 2nd cycle
        if(shift_ena !== 1) $display("Test Failed: `shift_ena` should be asserted for 4 cycles");
        @(negedge clk); // 3rd cycle
        if(shift_ena !== 1) $display("Test Failed: `shift_ena` should be asserted for 4 cycles");
        @(negedge clk); // 4th cycle
        if(shift_ena !== 1) $display("Test Failed: `shift_ena` should be asserted for 4 cycles");

        // Now, shift_ena should deassert
        @(negedge clk);
        if(shift_ena !== 0) $display("Test Failed: `shift_ena` did not deassert after 4 cycles");

        // Simulate counting operation
        @(negedge clk);
        counting_reg = 1;
        done_counting = 0;

        // Check done is still not asserted
        @(negedge clk);
        if(counting !== 1) $display("Test Failed: `counting` should be asserted while counting");
        done_counting = 1; // Counters finish counting

        // Check done is asserted
        @(negedge clk);
        if(done !== 1) $display("Test Failed: `done` should be asserted after counting");

        // User acknowledges the done signal
        ack = 1;
        @(negedge clk);
        if(done !== 0) $display("Test Failed: `done` should be deasserted after `ack`");
        ack = 0;

        // Check reset behavior after ack
        @(negedge clk);
        reset = 1;
        @(negedge clk);
        reset = 0;
        if(shift_ena !== 0 || counting_reg !== 0 || done !== 0) $display("Test Failed: Reset does not work properly");

        // All tests passed
        $display("All test cases passed");

        // Finish simulation
        $finish;
    end

    // Helper task to send data bits
    task send_pattern;
        input bit val;
        begin
            @(negedge clk) data = val;
        end
    endtask

endmodule

