module testbench;
  reg clk;
  reg x;
  wire z;

  // Instantiate the Device Under Test (DUT)
  top_module dut (
    .clk(clk),
    .x(x),
    .z(z)
  );

  // Clock generation
  initial begin
    clk = 0;
    forever #10 clk = ~clk;
  end

  // Stimulus here
  initial begin
    // Initialize inputs
    x = 0;

    // Reset sequence
    #5;
    x = 0; 
    #20;
    x = 1; // Test flip after several clock cycles with input changes
    #20;
    x = 0;
    #20;
    x = 1;
    #20;
    x = 0;
    #20;
    x = 1;
    #40; // wait for multiple cycles

    // Evaluate results
    if (z !== calculate_expected_z(x))
      $display("Error: Incorrect output for input combination");
    else
      $display("all test cases passed");

    #10;
    $finish;
  end

  // Function to calculate the expected z based on x only
  function calculate_expected_z;
    input x;
    reg dff_x1, dff_x2, dff_x3;
    begin
      // Assuming x is stable over one clock period for simplicity
      dff_x1 = x;                      // From XOR DFF
      dff_x2 = (x & ~dff_x2);          // From AND DFF
      dff_x3 = (x | ~dff_x3);          // From OR DFF

      // XNOR logic on all three DFF outputs
      calculate_expected_z = ~(dff_x1 ^ dff_x2 ^ dff_x3);
    end
  endfunction
endmodule

