module testbench;
    reg clk, w, R, E, L;
    wire Q;
    integer errors = 0;

    // Instantiate the Unit Under Test (UUT)
    top_module uut (
        .clk(clk),
        .w(w),
        .R(R),
        .E(E),
        .L(L),
        .Q(Q)
    );

    // Clock generation
    always begin
        clk = 0; #5; clk = 1; #5;
    end

    // Test procedure
    initial begin
        // Test case 1: Load Functionality
        w = 0; R = 1; E = 0; L = 1;
        #10; // Wait for a clock edge
        if (Q !== R) begin
            $display("Test Case 1 Failed: Load functionality does not match.");
            errors = errors + 1;
        end

        // Test case 2: Shift Functionality
        w = 1; R = 0; E = 1; L = 0;
        #10; // Wait for next clock edge
        if (Q !== 1) begin
            $display("Test Case 2 Failed: Shift functionality does not match when E is high.");
            errors = errors + 1;
        end

        // Test case 3: Hold Functionality (E is low, should hold the value irrespective of w)
        w = 0; R = 0; E = 0; L = 0;
        #10; // Wait for next clock edge
        if (Q !== 1) begin
            $display("Test Case 3 Failed: Hold functionality does not match when E is low.");
            errors = errors + 1;
        end

        // Final report
        if (errors == 0) begin
            $display("all test cases passed");
        end

        // Finish simulation
        $finish;
    end

