module testbench;
    // Inputs
    reg d;
    reg done_counting;
    reg ack;
    reg [9:0] state;

    // Outputs
    wire B3_next;
    wire S_next;
    wire S1_next;
    wire Count_next;
    wire Wait_next;
    wire done;
    wire counting;
    wire shift_ena;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .d(d),
        .done_counting(done_counting),
        .ack(ack),
        .state(state),
        .B3_next(B3_next),
        .S_next(S_next),
        .S1_next(S1_next),
        .Count_next(Count_next),
        .Wait_next(Wait_next),
        .done(done),
        .counting(counting),
        .shift_ena(shift_ena)
    );

    initial begin
        // Initialize Inputs
        d = 0;
        done_counting = 0;
        ack = 0;
        state = 10'b0000000001; // Initial state S

        // Wait for global reset
        #5;

        // Test Case 0: Reset condition
        if(!B3_next && !S_next && !S1_next && !Count_next && !Wait_next && !done && !counting && !shift_ena)
            $display("Test Case 0 Passed");
        else begin
            $display("Test Case 0 Failed");
            $finish;
        end

        // Test transitions based on state diagram
        // Move from S to S1
        d = 1; state = 10'b0000000001; #10;
        if(S1_next)
            $display("Test S to S1 Passed");
        else begin
            $display("Test S to S1 Failed");
            $finish;
        end

        // Move from S1 to S11
        d = 1; state = 10'b0000000010; #10;
        if(state[2])  // S11 encoded as 10'b0000000100
            $display("Test S1 to S11 Passed");
        else begin
            $display("Test S1 to S11 Failed");
            $finish;
        end

        // Further cases can be added similarly
        // Example of testing shifting states B0 to B3
        d = 1; state = 10'b0010000000; #10;  // State B0
        if(shift_ena && B3_next)
            $display("Test B0 to B1 Passed");
        else begin
            $display("Test B0 to B1 Failed");
            $finish;
        end

        // Test output logic in final state (Wait)
        done_counting = 1; state = 10'b0100000000; // Count
        #10;
        done_counting = 0; ack = 1; state = 10'b1000000000; // Wait
        #10;
        if(done && ack && Wait_next)  // Continue in Wait till ack=0
            $display("Test Count to Wait Passed");
        else begin
            $display("Test Count to Wait Failed");
            $finish;
        end

        // Re-setting to move to initial state S
        ack = 0; state = 10'b0000000001; #10;

        // If no finish has been called, all tests passed
        $display("all test cases passed");
    end
endmodule

