module tb_top_module;

    // Inputs
    reg [7:0] in;
    // Outputs
    wire [2:0] pos;

    // Instantiate the Device Under Test (DUT)
    top_module uut (
        .in(in), 
        .pos(pos)
    );

    // Test cases result tracker
    reg all_tests_passed;

    // Initialize Inputs and monitor changes
    initial begin
        $monitor("Time=%t, in=%b, pos=%d", $time, in, pos);
        all_tests_passed = 1'b1; // Assume all tests will pass initially

        // Test cases
        in = 8'b00000001; #10; check_results(3'd0);
        in = 8'b00000010; #10; check_results(3'd1);
        in = 8'b00000100; #10; check_results(3'd2);
        in = 8'b00001000; #10; check_results(3'd3);
        in = 8'b00010000; #10; check_results(3'd4);
        in = 8'b00100000; #10; check_results(3'd5);
        in = 8'b01000000; #10; check_results(3'd6);
        in = 8'b10000000; #10; check_results(3'd7);
        in = 8'b00000000; #10; check_results(3'd0); // No bit set, expecting 0
        in = 8'b11000000; #10; check_results(3'd6); // More than one bit set, first (least significant) one is 6 
        in = 8'b10010000; #10; check_results(3'd4);

        // Final evaluation of test results
        if (all_tests_passed) begin
            $display("all test cases passed");
        end

        // End simulation
        $finish;
    end

    // Task to check results and update all_tests_passed flag
    task check_results;
        input [2:0] expected_pos;
        begin
            if (pos !== expected_pos) begin
                all_tests_passed = 1'b0;
            end
        end
    endtask

endmodule

