            #10;  // Wait for any internal processing

            if (out !== expected) begin
                $display("Test case failed for inputs: a=%b, b=%b, c=%b, d=%b. Expected out=%b, got out=%b", a, b, c, d, expected, out);
                error_flag = 1;
            end
        end

        if (error_flag == 0)
            $display("all test cases passed");

        $finish; // End simulation
    end
