    wire [7:0] pedge;
    reg [7:0] expected_pedge;

    // Instantiate the Device Under Test (DUT)
    top_module DUT (
        .clk(clk),
        .in(in),
        .pedge(pedge)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Generate a clock with 10ns period (100 MHz)
    end

    // Test cases
    initial begin
        // Initialize inputs
        in = 8'b00000000;
        #10;

        // Test case 1: No change
        in = 8'b00000000;
        expected_pedge = 8'b00000000;
        #10; // Wait one clock period
        check_pedge();

        // Test case 2: Single bit 0 to 1 transition
        in = 8'b00000001;
        #10; // Transition occurs
        expected_pedge = 8'b00000001;
        #10; // Wait one cycle after the transition
        check_pedge();

        // Test case 3: Multiple bits 0 to 1 transition
        in = 8'b11001100;
        #10; // Transition occurs
        expected_pedge = 8'b11001100;
        #10; // Wait one cycle after the transition
        check_pedge();

        // Test case 4: Random transitions
        in = 8'b10101010; 
        #10; // Transition occurs
        expected_pedge = 8'b10101010;
        #10; // Wait one cycle after the transition
        check_pedge();

        // Assess overall success
        if (tests_passed) begin
            $display("all test cases passed");
        end
        $finish;
    end

    // Check outputs against expectations
    task check_pedge;
        if (pedge !== expected_pedge) begin
            $display("Test case failed at time %t, expected %b, received %b", $time, expected_pedge, pedge);
            tests_passed = 0;
        end
    endtask

    // Test status
    integer tests_passed = 1; // Assume tests passed unless a test case fails

endmodule

