    top_module uut (
        .clk(clk),
        .in(in),
        .out(out)
    );

    // Initialize all variables
    initial begin
        clk = 0;
        in = 0;

        // Generate clock
        forever #5 clk = ~clk; // Clock with a period of 10ns
    end

    // Test cases
    initial begin
        // Initialize Inputs
        clk = 0;
        in = 0;

        // Wait for global reset
        #100;        

        // Test Case 1: Check the initial condition of "out" (should be 0)
        if (out !== 0) $display("Test Case Failed: Initial condition of out is not 0");

        // Test Case 2: Simple toggle check
        @(posedge clk);
        in = 1;
        @(posedge clk);
        if (out !== 1) $display("Test Case Failed: Toggle test 1 failed");

        @(posedge clk);
        in = 0;
        @(posedge clk);
        if (out !== 1) $display("Test Case Failed: Toggle test 2 failed");

        @(posedge clk);
        in = 1;
        @(posedge clk);
        if (out !== 0) $display("Test Case Failed: Toggle test 3 failed");

        @(posedge clk);
        in = 1;
        @(posedge clk);
        if (out !== 1) $display("Test Case Failed: Toggle test 4 failed");

        // All test cases passed
        #10; // small delay to process last test cases
        if (!display_triggered) $display("All test cases passed");

        // Stop simulation
        $finish;
    end

    // Mechanism to avoid displaying "All test cases passed" message upon test failure
    reg display_triggered = 0; // Corrected initial declaration
    always @(negedge clk) begin
        if ($time > 0 && $realtime > 1) display_triggered = 1; // Corrected conditional and delay syntax
    end
