module testbench;
    // Inputs
    reg clk;
    reg reset;
    reg ena;

    // Outputs
    wire pm;
    wire [7:0] hh;
    wire [7:0] mm;
    wire [7:0] ss;

    // Instantiate the Unit Under Test (UUT)
    top_module uut (
        .clk(clk),
        .reset(reset),
        .ena(ena),
        .pm(pm),
        .hh(hh),
        .mm(mm),
        .ss(ss)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Generate a clock with period 10ns
    end

    // Test variables
    integer failures = 0;

    // Check procedure definition
    task check_values;
        input reg pm_expected;
        input [7:0] hh_expected;
        input [7:0] mm_expected;
        input [7:0] ss_expected;
        begin
            if (pm !== pm_expected || hh !== hh_expected || mm !== mm_expected || ss !== ss_expected) begin
                $display("Test failed at time %t", $time);
                failures = failures + 1;
            end
        end
    endtask

    // Begin testing
    initial begin
        // Initialize Inputs
        reset = 0;
        ena = 0;

        // Apply a reset
        #20;
        reset = 1; #10;
        reset = 0;

        // TEST CASE 1: After reset, check 12:00 AM
        check_values(0, 8'h01, 8'h00, 8'h00);

        // Enable clock to run, simulate for a few seconds and tests
        ena = 1;

        // TEST CASE 2: After 1 second
        #1000;
        check_values(0, 8'h01, 8'h00, 8'h01);

        // TEST CASE 3: After 59 seconds
        #58000;
        check_values(0, 8'h01, 8'h01, 8'h00); // 1:01:00 AM

        // TEST CASE 4: More complex time progression (example: 11:59 PM to 12:00 AM)
        #41402000;
        check_values(1, 8'h11, 8'h59, 8'h59);
        #1000;
        check_values(0, 8'h12, 8'h00, 8'h00);  // Resets to 12:00 AM

        // Disable for a second, then re-enable and test transition
        #500;
        ena = 0;
        #500;
        ena = 1;
        #1000;
        check_values(0, 8'h12, 8'h00, 8'h01);  // Ensures it holds state correctly when disabled

        // End of test series. Check if all passed.
        #10;
        if (failures === 0) $display("All test cases passed");
        $finish;
    end

endmodule

