module testbench;

    reg clk, j, k, reset;
    wire out;

    top_module DUT(
        .clk(clk),
        .j(j),
        .k(k),
        .reset(reset),
        .out(out)
    );

    initial begin
        clk = 0;
        forever #10 clk = ~clk;  // Clock with period 20ns
    end

    task initialize_inputs;
        begin
            j = 0;
            k = 0;
            reset = 0;
        end
    endtask

    task apply_reset;
        begin
            // Active-high synchronous reset
            @(posedge clk);
            reset = 1; 
            @(posedge clk);
            reset = 0;
        end
    endtask

    task check_state(
        input reg expected_out,
        input string message
    );
        begin
            if(out !== expected_out) begin
                $display("Test failed at state: %s", message);
                $finish;
            end
        end
    endtask

    integer total_tests = 0;
    integer tests_passed = 0;

    initial begin
        initialize_inputs();
        $display("Starting Test...");

        apply_reset();
        check_state(0, "RESET to OFF");
        total_tests = total_tests + 1;

        // Test: OFF -> ON
        j = 1; k = 0;
        @(posedge clk);
        check_state(1, "OFF to ON");
        tests_passed = tests_passed + 1;
        total_tests = total_tests + 1;

        // Test: ON -> ON
        j = 0; k = 0;
        @(posedge clk);
        check_state(1, "ON stays ON");
        tests_passed = tests_passed + 1;
        total_tests = total_tests + 1;

        // Test: ON -> OFF
        j = 0; k = 1;
        @(posedge clk);
        check_state(0, "ON to OFF");
        tests_passed = tests_passed + 1;
        total_tests = total_tests + 1;

        // Check all tests passed
        if (tests_passed === total_tests) begin
            $display("all test cases passed");
        end

        $finish;
    end

endmodule

