module testbench;

  // Inputs to Device Under Test
  reg clk;
  reg slowena;
  reg reset;

  // Outputs from Device Under Test
  wire [3:0] q;

  // Instantiate the Device Under Test (DUT)
  top_module dut(
      .clk(clk),
      .slowena(slowena),
      .reset(reset),
      .q(q)
  );

  // Clock generation
  initial begin
    clk = 0;
    forever #5 clk = ~clk;
  end

  // Test scenario
  initial begin
    // Initialize inputs
    reset = 0;
    slowena = 0;

    // Apply reset
    @(posedge clk)
      reset = 1;
    @(posedge clk)
      reset = 0;

    // Check reset functionality
    if (q !== 0) begin
      $display("Reset failed to set q to 0.");
      $finish;
    end

    // Test counter increment
    @(posedge clk)
      slowena = 1;  // Enable counting

    // Check counter increments for 10 clock cycles
    for (integer i = 1; i <= 10; i++) begin
      @(posedge clk);
      if (q !== (i % 10)) begin
        $display("Counter failed at i = %d, q = %d", i, q);
        $finish;
      end
    end

    // Test pause functionality
    @(posedge clk)
      slowena = 0;  // Disable counting
    @(posedge clk);
    @(posedge clk);
    if (q !== 0) begin
      $display("Pause functionality failed, q = %d", q);
      $finish;
    end

    // Resume counting
    @(posedge clk)
      slowena = 1;
    @(posedge clk);
    if (q !== 1) begin
      $display("Resume counting failed, q = %d", q);
      $finish;
    end

    // Output success
    $display("all test cases passed");

    $finish;
  end

endmodule

