    top_module dut (
        .clk(clk),
        .reset(reset),
        .data(data),
        .count(count),
        .counting(counting),
        .done(done),
        .ack(ack)
    );

    // Clock generation
    always #5 clk = ~clk; // 100 MHz Clock

    // Test cases
    initial begin
        $monitor("Time=%t, clk=%b, reset=%b, data=%b, count=%d, counting=%b, done=%b, ack=%b", $time, clk, reset, data, count, counting, done, ack);
        clk = 0;
        reset = 1;
        data = 0;
        ack = 0;
        #10;    // Allow reset to process

        reset = 0;

        // Test Case 1: Pattern Recognition
        // Sending sequence: x x x x 1101 and additional four bits
        #20; data = 0;
        #10; data = 1;
        #10; data = 1;
        #10; data = 0;
        #10; data = 1;
        // Delay = 4'b0101 (5), so count = 6000 cycles
        #10; data = 0; 
        #10; data = 1; 
        #10; data = 0; 
        #10; data = 1;

        // Wait for counting start
        #100;

        assert(count == 5) else $display ("Test Case 1 Failed: Incorrect count value after triggering");

        // Simulate counting for exact 6000 cycles
        #6000;
        assert(done == 1) else $display("Test Case 1 Failed: Counting did not complete correctly");

        // Acknowledge the completion
        ack = 1;
        #100; // Allow ack to be processed
        ack = 0;

        // Resetting for next pattern
        reset = 1; #20; reset = 0;

        #100;  // Additional time for other pending operations

        // Confirm that design resumed looking for next sequence or has stopped completely
        data = 1; #10; data = 1; #10; data = 0; #10; data = 1;

        // Check if the system properly restarts
        #5000;  // Arbitrary time to check if counting correctly finishes
        if (count == 0 && done != 1)
