module tb_top_module();

    reg clk;
    reg reset;
    reg data;
    wire start_shifting;

    // Instantiate the Device Under Test (DUT)
    top_module DUT (
        .clk(clk),
        .reset(reset),
        .data(data),
        .start_shifting(start_shifting)
    );

    // Clock process
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // 100MHz Clock
    end

    // Stimuli process
    initial begin
        // Test case initialization
        data = 0;
        reset = 1; // Apply reset
        #10; // Wait for reset to take effect
        reset = 0;

        // Sequence input test cases
        // Input different sequences and check for 'start_shifting'
        @(posedge clk) data = 1;
        @(posedge clk) data = 1;
        @(posedge clk) data = 0;
        @(posedge clk) data = 1; // Sequence 1101 occurs here
        #10; // Wait to allow state transition
        if (start_shifting !== 1'b1) begin
            $display("Test Case Fail: 1101 sequence failed to set start_shifting");
            $finish;
        end

        // Verify hold of start_shifting
        @(posedge clk) data = 1;
        @(posedge clk) data = 0;
        @(posedge clk) data = 0;
