    top_module dut (
        .clk(clk),
        .resetn(resetn),
        .r(r),
        .g(g)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk; // Clock with a period of 10ns
    end

    // Initial stimulus
    initial begin
        // Initialize inputs
        resetn = 0;
        r = 3'b000;
        #10;

        // Release reset
        resetn = 1;
        #10;

        // Test Sequence
        // State A transitions
        r = 3'b000; // A to A
        #20;
        check_state(3'b000);

        r = 3'b100; // A to B
        #20;
        check_state(3'b001);

        r = 3'b010; // B to A (since r1 = 0)
        #20;
        check_state(3'b000);

        r = 3'b010; // A to C
        #20;
        check_state(3'b010);

        r = 3'b000; // C to A (since r2 = 0)
        #20;
        check_state(3'b000);

        r = 3'b001; // A to fault state (since r3)
        #20;
        check_state(3'b100);

        r = 3'b000; // Some invalid state to A (since no requests)
        #20;
        check_state(3'b000);

        // Finish test, evaluate results
        $finish;
    end

    // Task to check current state
    task check_state;
        input [3:1] expected_g;
        begin
            if (g !== expected_g) begin
                $display("Test failed. Expected %b, got %b", expected_g, g);
            end
        end
    endtask  // Corrected: Replaced 'end' with 'endtask' to properly close task definition

    initial begin
        // Wait for the testing to complete and check if all tests are passed
        #200; // Wait for sufficient simulation time
        if (!$isunknown(g) && g == 3'b000) // Check for the ideal final state
            $display("all test cases passed");
    end
