    @(posedge clk) a = 1; b = 0;
    repeat (2) @(posedge clk);
    @(posedge clk) a = 0; b = 1;
    repeat (2) @(posedge clk);
    @(posedge clk) a = 0; b = 0;
    repeat (15) @(posedge clk);
    $finish;
  end

  // Check output against expected values
  reg test_fail = 0;

  initial begin
    // Capture the moment right before state/output comparisons
    @(negedge clk); // align with neg edge of clk to check values are stable
    // Sequentially check values at each important time step
    // Graphical table aids in comparing specific time snap values
