    top_module dut(
        .in(in),
        .state(state),
        .next_state(next_state),
        .out(out)
    );

    // Test variables
    integer i;
    reg [1:0] expected_next_state;
    reg expected_out;
    reg errors = 0;

    // Test case procedure
    task perform_test;
        input reg test_in;
        input reg [1:0] test_state;
        input reg [1:0] test_expected_next_state;
        input reg test_expected_out;
        begin
            in = test_in;
            state = test_state;
            #10; // Wait for the output to stabilize

            if (next_state !== test_expected_next_state || out !== test_expected_out) begin
                $display("Test failed for state %b, input %b", test_state, test_in);
                errors = 1;
            end
        end
    endtask

    // Test sequence
    initial begin
        // Test for state A
        perform_test(0, 2'b00, 2'b00, 0); // A with in=0
        perform_test(1, 2'b00, 2'b01, 0); // A with in=1

        // Test for state B 
        perform_test(0, 2'b01, 2'b10, 0); // B with in=0
        perform_test(1, 2'b01, 2'b01, 0); // B with in=1

        // Test for state C
        perform_test(0, 2'b10, 2'b00, 0); // C with in=0
        perform_test(1, 2'b10, 2'b11, 0); // C with in=1

        // Test for state D
        perform_test(0, 2'b11, 2'b10, 1); // D with in=0
        perform_test(1, 2'b11, 2'b01, 1); // D with in=1

        #10; // Add delay to ensure all cases are tested

        if(errors == 0) begin
            $display("All test cases passed.");
        end

        $finish;
    end

