module testbench;

    reg clk;
    reg [7:0] in;
    reg reset;
    wire [23:0] out_bytes;
    wire done;

    // Instantiate the Device Under Test (DUT)
    top_module dut(
        .clk(clk),
        .in(in),
        .reset(reset),
        .out_bytes(out_bytes),
        .done(done)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;
    end

    // Test vectors
    initial begin
        $display("Testbench started...");

        reset = 1; in = 8'h00; #10;
        reset = 0; in = 8'h2C; #10;
        in = 8'h81; #10;
        in = 8'h09; #10;
        in = 8'h6B; #10;
        in = 8'h0D; #10;
        in = 8'h8D; #10;
        in = 8'h6D; #10;
        in = 8'h12; #10;
        in = 8'h01; #10;
        in = 8'h76; #10;
        in = 8'h3D; #10;
        in = 8'hED; #10;
        in = 8'h8C; #10;
        in = 8'hF9; #10;
        in = 8'hCE; #10;
        in = 8'hC5; #10;
        in = 8'hAA; #10;
        $finish;
    end

    // Tracking test cases
    reg [23:0] expected_out_bytes;
    integer num_of_test = 0, num_of_pass = 0;

    always @(posedge clk) begin
        if (reset) begin
            num_of_test = 0; 
            num_of_pass = 0;
        end
        else begin
            num_of_test = num_of_test + 1;
            case (num_of_test)
                1: expected_out_bytes = 24'h2C8109;
                2: expected_out_bytes = 24'h6B0D8D;
                3: expected_out_bytes = 24'h6D1201;
                4: expected_out_bytes = 24'hD763D;
                5: expected_out_bytes = 24'hED8CF9;
                default: expected_out_bytes = 24'hXXXXXX;
            endcase

            if (done && (out_bytes !== expected_out_bytes)) begin
                $display("Test case %d failed: Expected %h, Got %h", num_of_test, expected_out_bytes, out_bytes);
            end else if (done) begin
                num_of_pass = num_of_pass + 1;
            end
        end
    end

    initial begin
        #200 if (num_of_pass == 5) $display("all test cases passed");
    end

endmodule

