module testbench;
    reg in;
    reg [9:0] state;
    wire [9:0] next_state;
    wire out1, out2;
    integer i;

    // Instantiate the Device Under Test (DUT)
    top_module DUT (
        .in(in),
        .state(state),
        .next_state(next_state),
        .out1(out1),
        .out2(out2)
    );

    // Provide input stimulus and check behavior
    initial begin
        // Initialize variables
        in = 0;
        state = 10'b0000000001; // S0

        // Apply a sequence of inputs corresponding to transitions
        for (i = 0; i < 50; i = i + 1) begin
            // Apply input and toggle after 10ns
            #10 in = i % 2;
        end

        // Complete the simulation
        #100
        $finish;
    end

    // Monitor states and output transitions
    always @(posedge in or negedge in) begin
        // Check state transitions and outputs as specified in problem description
        case(state)
            10'b0000000001: begin // S0
                if (in == 0) $display("State S0, Expected next_state: S0");
                else $display("State S0, Expected next_state: S1");
            end
            10'b0000000010: begin // S1
                if (in == 0) $display("State S1, Expected next_state: S0");
                else $display("State S1, Expected next_state: S2");
            end
            10'b0000000100: begin // S2
                if (in == 0) $display("State S2, Expected next_state: S0");
                else $display("State S2, Expected next_state: S3");
            end
            10'b0000001000: begin // S3
                if (in == 0) $display("State S3, Expected next_state: S0");
                else $display("State S3, Expected next_state: S4");
            end
            10'b0000010000: begin // S4
                if (in == 0) $display("State S4, Expected next_state: S0");
                else $display("State S4, Expected next_state: S5");
            end
            10'b0000100000: begin // S5
                if (in == 0) $display("State S5, Expected next_state: S8");
                else $display("State S5, Expected next_state: S6");
            end
            10'b0001000000: begin // S6
                if (in == 0) $display("State S6, Expected next_state: S9");
                else $display("State S6, Expected next_state: S7");
            end
            10'b0010000000: begin // S7
                if (in == 0) $display("State S7, Expected next_state: S0");
                else $display("State S7, Expected next_state: S7");
            end
            10'b0100000000: begin // S8
                if (in == 0) $display("State S8, Expected next_state: S0");
                else $display("State S8, Expected next_state: S1");
            end
            10'b1000000000: begin // S9
                if (in == 0) $display("State S9, Expected next_state: S0");
                else $display("State S9, Expected next_state: S1");
            end
        endcase
    end

    // Check outputs and next_state
    always @(state or next_state or out1 or out2) begin
        if (next_state == 10'b0000000001) $display("at S0");
        else if (next_state == 10'b0000000010) $display("at S1");
        else if (next_state == 10'b0000000100) $display("at S2");
        else if (next_state == 10'b0000001000) $display("at S3");
        else if (next_state == 10'b0000010000) $display("at S4");
        else if (next_state == 10'b0000100000) $display("at S5");
        else if (next_state == 10'b0001000000) $display("at S6");
        else if (next_state == 10'b0010000000) $display("at S7");
        else if (next_state == 10'b0100000000) $display("at S8");
        else if (next_state == 10'b1000000000) $display("at S9");
        else $display("ERROR: Unexpected state");

        // Check if any output state fails
        if ((state == 10'b0010000000) && (out1 !== 0 || out2 !== 1)) $display("ERROR: Output at S7 is incorrect");
        if ((state == 10'b0100000000) && (out1 !== 1 || out2 !== 0)) $display("ERROR: Output at S8 is incorrect");
        if ((state == 10'b1000000000) && (out1 !== 1 || out2 !== 1)) $display("ERROR: Output at S9 is incorrect");
    end
endmodule

