    #80 reset = 1; // Assert reset again
    #10 reset = 0; // De-assert reset
    #100 $finish; // End simulation
  end

  // Reporting 
  reg [3:0] expected_shift_ena;
  reg [3:0] cycle_count;
  initial begin
    expected_shift_ena = 4'b1111;
    cycle_count = 0;
  end

  always @(posedge clk) begin
    if (reset) begin
      if(cycle_count < 4) begin
        cycle_count <= cycle_count + 1;
        if(shift_ena !== 1'b1) begin
          $display("Test Case Failed at cycle: %d", cycle_count);
          $finish; 
        end
      end else begin
        if(shift_ena !== 1'b0) begin
          $display("Test Case Failed in low phase at cycle: %d", cycle_count);
          $finish;
        end
      end
    end else begin
      cycle_count <= 0; // Reset cycle counter when not in reset
    end
  end

  // Monitor at the end of all stimuli to confirm all test cases passed
  initial begin
    #180; // Wait for all cycles of interest to pass
    $display("all test cases passed");
  end

endmodule

