module testbench;

reg clk;
reg areset;
reg train_valid;
reg train_taken;
wire [1:0] state;

top_module DUT (
    .clk(clk),
    .areset(areset),
    .train_valid(train_valid),
    .train_taken(train_taken),
    .state(state)
);

// clock generation
initial begin
    clk = 0;
    forever #5 clk = ~clk; // 100MHz clock, 10ns period
end

integer i;
reg [1:0] expected_state;

// Test cases
initial begin
    $display("Starting test cases...");
    areset = 1; train_valid = 0; train_taken = 0; #11; // Reset the DUT asynchronously
    areset = 0;

    // Test Case 1: Check reset value
    if (state !== 2'b01) begin
        $display("TC1 Failed: Reset to weakly not-taken failed, state=%b", state);
        $finish;
    end

    // Test Case 2: Increment to 3
    {train_valid, train_taken} = 2'b11;
    expected_state = 2'b01;
    for (i = 0; i < 2; i = i + 1) begin
        #10;
        if (state !== expected_state + i + 1) begin
            $display("TC2 Failed: Increment check failed, state=%b, expected=%b", state, expected_state + i + 1);
            $finish;
        end
    end

    // Test Case 3: Hold at max value
    #10;
    if (state !== 2'b11) begin
        $display("TC3 Failed: Max saturation test failed, state=%b", state);
        $finish;
    end

    // Test Case 4: Decrement to 0
    {train_valid, train_taken} = 2'b10;
    expected_state = 2'b11;
    for (i = 0; i < 3; i = i + 1) begin
        #10;
        if (state !== expected_state - i - 1) begin
            $display("TC4 Failed: Decrement check failed, state=%b, expected=%b", state, expected_state - i - 1);
            $finish;
        end
    end

    // Test Case 5: Hold at min value
    #10;
    if (state !== 2'b00) begin
        $display("TC5 Failed: Min saturation test failed, state=%b", state);
        $finish;
    end

    // Test Case 6: Ensure stability when train_valid = 0
    {train_valid, train_taken} = 2'b00;
    #10
    if (state !== 2'b00) begin
        $display("TC6 Failed: Stability check when train_valid=0 failed, state=%b", state);
        $finish;
    end

    // If all tests pass
    $display("All test cases passed");
    $finish;
end

endmodule

