    .out_xor(out_xor)
);

// Test vector
integer i;
reg expected_and, expected_or, expected_xor;
reg test_error = 0;

initial begin
    // Test all zeroes
    in = 100'b0;
    #1;
    check_results(&in, |in, ^in);

    // Test all ones
    in = ~100'b0;
    #1;
    check_results(&in, |in, ^in);

    // Test alternating ones and zeros
    for (i = 0; i < 100; i = i + 1) begin
        in[i] = i % 2;
    end
    #1;
    check_results(&in, |in, ^in);

    // Test single high bits
    for (i = 0; i < 100; i = i + 1) begin
        in = 100'b0;
        in[i] = 1;
        #1;
        check_results(&in, |in, ^in);
    end

    // Check and report the result
    if (!test_error) begin
        $display("all test cases passed");
    end

    $finish;
end

// Utility task to check output and compare
task check_results;
    input reg expected_and;
    input reg expected_or;
    input reg expected_xor;
    begin
        if (out_and !== expected_and || out_or !== expected_or || out_xor !== expected_xor) begin
            test_error = 1; // Indicate there was an error
            $display("Test failed for input %b: Expected AND %b, OR %b, XOR %b, got AND %b, OR %b, XOR %b",
                         in, expected_and, expected_or, expected_xor, out_and, out_or, out_xor);
        end
    end
endtask

