    reg x;

    // Outputs
    wire z;

    // Instantiate the Unit Under Test (DUT)
    top_module uut (
        .clk(clk),
        .aresetn(aresetn),
        .x(x),
        .z(z)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // Clock period of 10ns
    end

    // Test cases
    initial begin
        // Initialize Inputs
        aresetn = 0;  // Apply reset
        x = 0;

        // Remove reset and initiate test
        #15;
        aresetn = 1;  // Release reset at negative edge
        @(negedge clk);

        // Test sequence 1: 101 matches
        x = 1; @(negedge clk);
        x = 0; @(negedge clk);
        x = 1; @(negedge clk);
        if (z !== 1) $display("Test case failed.");

        // Test sequence 2: Overlapping sequence 10101
        x = 0; @(negedge clk);
        x = 1; @(negedge clk);
        if (z !== 1) $display("Test case failed.");

        // Test sequence 3: No-match sequence 00100
        x = 0; @(negedge clk);
        x = 0; @(negedge clk);
        x = 1; @(negedge clk);
        x = 0; @(negedge clk);
        x = 0; @(negedge clk);
        if (z !== 0) $display("Test case failed.");

        // Test sequence 4: Partial sequence followed by reset
        x = 1; @(negedge clk);
        x = 0; @(negedge clk);
        aresetn = 0; @(negedge clk);
        x = 1; @(negedge clk);
        if (z !== 0) $display("Test case failed.");
        aresetn = 1; @(negedge clk);

        // All tests passed
        $display("all test cases passed");

        // Finish test
        $finish;
    end

endmodule

