module testbench;
    // Inputs
    reg sel;
    reg [7:0] a;
    reg [7:0] b;

    // Output
    wire [7:0] out;

    // Instantiate the Unit Under Test (UUT)
    top_module uut (
        .sel(sel),
        .a(a),
        .b(b),
        .out(out)
    );

    // Variables for test status
    integer errors = 0;
    integer test_cases = 0;

    // Test case procedure
    task testcase;
        input sel_t;
        input [7:0] a_t;
        input [7:0] b_t;
        input [7:0] expected_out;
        begin
            sel = sel_t;
            a = a_t;
            b = b_t;
            #10; // Wait for any propagation delays

            if (out !== expected_out) begin
                errors = errors + 1;
                $display("Test case failed: sel=%b, a=%h, b=%h, Expected=%h, Received=%h", sel_t, a_t, b_t, expected_out, out);
            end

            test_cases = test_cases + 1;
        end
    endtask

    // Initialize Inputs
    initial begin
        // Initialize Inputs
        sel = 0;
        a = 0;
        b = 0;

        // Wait 100 ns for global reset to finish
        #100;

        // Add stimulus here
        testcase(0, 8'hFF, 8'h00, 8'hFF); // Expect output a when sel is 0
        testcase(1, 8'hFF, 8'h00, 8'h00); // Expect output b when sel is 1
        testcase(0, 8'hAA, 8'h55, 8'hAA); // Repeat with different inputs
        testcase(1, 8'hAA, 8'h55, 8'h55); // Repeat with different inputs
        testcase(0, 8'h00, 8'hFF, 8'h00); // Edge cases
        testcase(1, 8'h00, 8'hFF, 8'hFF); // Edge cases

        #10; // Additional delay to complete all checks

        if (errors == 0) begin
            $display("all test cases passed");
        end

        $finish; // Finish the simulation
    end

endmodule

