module tb_top_module;

  reg clk, reset;
  wire [4:0] q;
  integer i;

  top_module DUT (
    .clk(clk),
    .reset(reset),
    .q(q)
  );

  // Clock generation
  always #5 clk = ~clk;

  initial begin
    // Initialize signals
    clk = 0;
    reset = 1;

    // Reset the system
    #10;
    reset = 0;
    #10;
    reset = 1;
    #10;
    reset = 0;

    // Check for the desired sequence
    // Expected maximum length cycle for a 5-bit LFSR with taps at positions 5 and 3 is 31 (2^5 - 1)
    // Sequence start at 1 as the LFSR is reset to 1
    // Verification of output (`q`) against the expected sequence values
    for (i = 0; i < 62; i = i + 1) begin
      @(posedge clk); // Move to next clock edge to sample `q`
      // After 31 clocks, expect the values to repeat because 5-bit maximum-length LFSR.
      if (i % 31 == 0 && q !== 5'b00001) begin
        $display("Test case failed at cycle %d, expected 1, got %b", i, q);
        $finish;
      end
    end

    // If no test fails print the success message
    $display("all test cases passed");
    $finish;
  end

endmodule

