module testbench;
    reg clk;
    reg d;
    wire q;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .clk(clk),
        .d(d),
        .q(q)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // Generate a clock with a period of 10 ns
    end

    // Test cases
    initial begin
        // Initialize Input
        d = 0;
        #2;  // Wait for 2ns to avoid edge coincidences

        // Check initial state
        if (q !== 0) $display("Test Case Failed at time %t with q=%d", $time, q);

        // Rising edge should trigger
        #8; d = 1;  // Change d before rising edge
        #2;  // Cross the rising edge
        if (q !== 1) $display("Test Case Failed at time %t with q=%d", $time, q);

        // Falling edge should trigger
        #8; d = 0;  // Change d before falling edge
        #2;  // Cross the falling edge
        if (q !== 0) $display("Test Case Failed at time %t with q=%d", $time, q);

        // Check with another set
        #8; d = 1;  // Change d before rising edge
        #2;  // Cross the rising edge
        if (q !== 1) $display("Test Case Failed at time %t with q=%d", $time, q);

        #8; d = 0;  // Change d before falling edge
        #2;  // Cross the falling edge
