module top_module_tb;

    reg clk;
    reg [7:0] in;
    reg reset;
    wire done;

    // Instantiate the Device Under Test (DUT)
    top_module dut (
        .clk(clk),
        .in(in),
        .reset(reset),
        .done(done)
    );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = !clk; // Toggle clock every 5 ns
    end

    // Initialize and drive signals
    initial begin
        reset = 1;       // Assert reset
        in = 8'b0000_0000;
        #10 reset = 0;   // Release reset

        // Test Case 1: First byte starting with bit 3 high followed by two extended bytes
        in = 8'b1000_0001; #10;  // Byte 1
        in = 8'b0111_1111; #10;  // Byte 2 
        in = 8'b0010_1010; #10;  // Byte 3

        if (done !== 1'b1) begin
            $display("Test Case 1 failed");
            $finish;
        end

        // Wait for next message detection cycle
        #10; 

        // Test Case 2: Misaligned first byte
        in = 8'b0100_1010; #10;  // Incorrect start
        in = 8'b1000_0011; #10;  // Byte 1
        in = 8'b0001_1100; #10;  // Byte 2
        in = 8'b0110_0111; #10;  // Byte 3

        if (done !== 1'b1) begin
            $display("Test Case 2 failed");
            $finish;
        end

        // Wait for next message detection cycle
        #10; 

        // Test Case 3: Correct sequence after a reset
        reset = 1; #10;
        reset = 0; #10;
        in = 8'b1000_1100; #10;  // Byte 1
        in = 8'b0000_1101; #10;  // Byte 2
        in = 8'b1100_1010; #10;  // Byte 3

        if (done !== 1'b1) begin
            $display("Test Case 3 failed");
            $finish;
        end

        $display("all test cases passed");
        $finish;
    end

