    initial begin
        clk = 0;
        forever #5 clk = ~clk; // 100MHz clock
    end

    // Test scenario
    initial begin
        reset = 1; // Assert reset
        #10;
        reset = 0; // Deassert reset

        // Checking reset condition
        if (q !== 16'b0) begin
            $display("Reset test failed, q = %b", q);
            $finish;
        end

        // Let the counter run and check enable outputs and counts
        for (i = 0; i < 10000; i = i + 1) begin
            #10; // Wait for a clock cycle
            // Check enable and count logic
            if ((q[3:0] == 9) && (ena[1] !== 1'b1)) begin
                $display("Enable or count logic test failed at q = %b, ena = %b", q, ena);
                $finish;
            end
            if ((q[7:4] == 9) && (q[3:0] == 9) && (ena[2] !== 1'b1)) begin
                $display("Enable or count logic test failed at q = %b, ena = %b", q, ena);
                $finish;
            end
            if ((q[11:8] == 9) && (q[7:4] == 9) && (q[3:0] == 9) && (ena[3] !== 1'b1)) begin
                $display("Enable or count logic test failed at q = %b, ena = %b", q, ena);
                $finish;
            end
            // For checking 9999 roll-over to 0000
            if (q == 16'd9999) begin
                #10;
                if (q !== 16'b0) begin
                    $display("Roll-over test failed, q did not roll over to 0000");
                    $finish;
                end
            end
        end

        $display("all test cases passed");
        $finish;
    end
