    always #5 clk = ~clk;

    // Test sequence
    initial begin
        // Initialize inputs
        a = 1'bx;
        @(posedge clk); #1;  // Wait for the first positive edge of clock

        // Test Case 1: q should follow a after one clock cycle delay when a is low.
        a = 0;
        expected_q = 1'bx;   // x state for comparison skipping
        @(posedge clk); 
        @(posedge clk); #1;  // Wait until the output is supposed to be stable
        expected_q = 1;
        if(q !== expected_q) pass_flag = 0;

        // Test Case 2: q should hold 1 when a repeatedly stays 0.
        a = 0;
        expected_q = 1;
        repeat(3) begin
            @(posedge clk); #1;
            if(q !== expected_q) pass_flag = 0;
        end

        // Test Case 3: q should change to 0 when a is set to 1.
        a = 1;
        expected_q = 1;    
        @(posedge clk); #1; 
        expected_q = 0;
        repeat(8) begin
            @(posedge clk); #1;
            if(q !== expected_q) pass_flag = 0;
        end

        // Report results
        if(pass_flag) $display("all test cases passed");

        // End simulation
        $finish;
    end
