module tb_top_module;

  reg clk;
  reg [7:0] in;
  wire [7:0] pedge;
  integer i;

  // Instantiate the Device Under Test (DUT)
  top_module dut(
    .clk(clk),
    .in(in),
    .pedge(pedge)
  );

  // Clock generation
  initial begin
    clk = 0;
    forever #5 clk = ~clk;
  end

  // Stimulus
  initial begin
    in = 8'b00000000; // Initial value
    #10;

    // Test cases
    in = 8'b00000001; #10;
    in = 8'b00000011; #10;
    in = 8'b00000111; #10;
    in = 8'b00001111; #10;
    in = 8'b00011111; #10;
    in = 8'b00111111; #10;
    in = 8'b01111111; #10;
    in = 8'b11111111; #10;
    in = 8'b00000000; #10; // Edge at every bit, finally back to 0
    in = 8'b10101010; #10; // Rapid changes
    in = 8'b01010101; #10;
    in = 8'b11110000; #10;
    in = 8'b00001111; #10;
    in = 8'b11111111; #10; // Stabilize high
    in = 8'b00000000; #20; // Check back to zeroes

    // Check results after the last simulation step
    #10 check_test_cases();
    $finish;
  end

  // Task to validate output
  task check_test_cases;
