    wire out;

    // Instantiate the Device Under Test (DUT)
    top_module DUT (
        .clk(clk),
        .j(j),
        .k(k),
        .areset(areset),
        .out(out)
    );

    // Generate clock with 10ns period
    initial begin
        clk = 0;
        forever #5 clk = ~clk;
    end

    // Test cases
    initial begin
        // Initialize all inputs
        j = 0;
        k = 0;
        areset = 0;

        // Apply asynchronous reset
        @(negedge clk) areset = 1;  
        @(posedge clk) areset = 0;

        // Test Case 1: OFF -> OFF (j=0)
        @(negedge clk) begin
            j = 0;
            k = 0;
            assert(out == 0) else $display("Test Case 1 Failed: Expected out=0");
        end

        // Test Case 2: OFF -> ON (j=1)
        @(negedge clk) j = 1;
        @(negedge clk) begin
            assert(out == 1) else $display("Test Case 2 Failed: Expected out=1");
            j = 0; // return to default
        end

        // Test Case 3: ON -> ON (k=0)
        @(negedge clk) begin
            k = 0;
            assert(out == 1) else $display("Test Case 3 Failed: Expected out=1");
        end

        // Test Case 4: ON -> OFF (k=1)
        @(negedge clk) k = 1;
        @(negedge clk) begin
            assert(out == 0) else $display("Test Case 4 Failed: Expected out=0");
            k = 0; // return to default
        end

        // Reset to see repeated sequence
        @(negedge clk) areset = 1;
        @(negedge clk) begin
            areset = 0;
            j = 0;
            k = 0;
        end

        // Reliable finish with reporting
        repeat (5) @(negedge clk); // Give time for additional state checks
        $display("all test cases passed");
        $finish;
    end
